import React from "react";
import '../../../../css/showMedicines.css'
class ShowMedicines extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            // needsPrescription: "",
            dontNeedsPrescription: "",
            data: null,
        };
    }

    componentWillMount() {
        let headers = new Headers();
        headers.set('Authorization', 'Basic ' + btoa(sessionStorage.getItem("email") + ":" + sessionStorage.getItem("password")));
        headers.set('Content-Type', 'application/json');
        fetch("http://localhost:8081/medicines", {
            headers: headers
        })
            .then(response => response.json())
            .then(data => {
                this.setState({ data: data });
                // let NeedsPrescription = [];
                // let DontNeedsPrescription = [];
                // data.forEach(element => {
                //     if (element.needsPrescription)
                //         NeedsPrescription.push(element)
                //     else
                //         DontNeedsPrescription.push(element)
                // })
                // if (NeedsPrescription.length !== 0)
                //     this.setState({needsPrescription: NeedsPrescription});
                // if (DontNeedsPrescription.length !== 0)
                //     this.setState({dontNeedsPrescription: DontNeedsPrescription});
            });
    }

    setDelete(name) {
        let headers = new Headers();
        headers.set('Authorization', 'Basic ' + btoa(sessionStorage.getItem('email') + ":" + sessionStorage.getItem('password')));
        headers.set('Content-Type', 'application/json');
        fetch("http://localhost:8081/medicines", {
            method: "DELETE",
            headers: headers,
            body: JSON.stringify(name)

        }).then(r => {
            if (r.status === 200) {
                window.alert("Medicine deleted!");
                window.location.reload()
            } else
                window.alert("Something unexpected happened! Try again!");
        })
    }

    render() {
        return (<div class="content" id="showMedicines">
            <h1>Medicines</h1>
            <hr></hr>
            {
                this.state.data ?
                    <div>
                        <table>
                            <tr>
                                <th>name</th>
                                <th>price</th>
                                <th>needs prescription</th>
                                <th>delete</th>
                            </tr>
                            {this.state.data.map((medicine, index) =>
                                <tr>
                                    <td>{medicine.name}</td>
                                    <td>{medicine.price}</td>
                                    <td>{medicine.needsPrescription ? "needs prescription" : "doesn't need prescription"}</td>
                                    <td>
                                        <button class="rmvBtn"
                                            onClick={() => this.setDelete([medicine.name])}>Delete
                                            Medicine
                                        </button>
                                    </td>
                                </tr>
                            )}
                        </table>

                    </div>
                    : <p>There is no medicines yet!</p>
            }
        </div>)
    }
}

export default ShowMedicines;
