import React, { useState } from 'react'
import { OneOffQrScanner } from 'react-webcam-qr-scanner.ts'

function DemoOneOffScanning() {
  const [qrCode, setQrCode] = useState('')

  function QrDetected(QrCode) {
    const machine = atob(QrCode);
    let headers = new Headers();
    headers.set('Authorization', 'Basic ' + btoa(sessionStorage.getItem("email") + ":" + sessionStorage.getItem("password")));
    headers.set('Content-Type', 'application/json');
    fetch(":8081/prescriptions/", {
      headers: headers
    })
      .then(response => response.json())
      .then(data => {
        let ValidData = [];
        let InvalidData = [];
        data.forEach(element => {
          if (element.valid)
            ValidData.push(element)
          else
            InvalidData.push(element)
        })
        if (ValidData.length !== 0)
          this.setState({ validData: ValidData });
        if (InvalidData.length !== 0)
          this.setState({ invalidData: InvalidData });
      });

  }

  return (
    <div className='content'>
      <h1>
        Scan Machine QR Code: <code>{QrDetected(qrCode)}</code>
      </h1>
      {/* auto close the webcam once detected QR code */}
      <OneOffQrScanner
        onQrCode={setQrCode}
        hidden={false} /* optional: set true to hide the video-preview */
      />
      {/* {QrDetected(qrCode)}   */}
    </div>
  )
}

export default DemoOneOffScanning
