import React from "react";
import '../../../../css/showPrescriptions.css'

class ShowBugs extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            data: ''
        };
    }

    componentWillMount() {

        let headers = new Headers();
        headers.set('Authorization', 'Basic ' + btoa(sessionStorage.getItem("email") + ":" + sessionStorage.getItem("password")));
        headers.set('Content-Type', 'application/json');
        fetch("http://localhost:8081/bugs", {
            headers: headers
        })
            .then(response => response.json())
            .then(data => {
                this.setState({ data: data })
            });
    }

    setOnClick(event) {
        if (event.target.nextSibling.style.display === 'none')
            event.target.nextSibling.style.display = 'block';
        else
            event.target.nextSibling.style.display = 'none';
    }

    setDelete(id) {
        let headers = new Headers();
        headers.set('Authorization', 'Basic ' + btoa(sessionStorage.getItem('email') + ":" + sessionStorage.getItem('password')));
        headers.set('Content-Type', 'application/json');
        fetch("http://localhost:8081/bugs", {
            method: "DELETE",
            headers: headers,
            body: JSON.stringify(id)

        }).then(r => {
            if (r.status === 200) {
                window.alert("Bug deleted!");
                window.location.reload()
            } else
                window.alert("Something unexpected happened! Try again!");
        })
    }


    render() {
        return (<div class="content">
            <h1>Bugs:</h1>
            <hr></hr>
            {
                this.state.data ?
                    this.state.data.map((element) =>
                        <div>
                            <button class="btn"
                                onClick={(event) => this.setOnClick(event)}>{"Bug " + element.id}</button>
                            <div style={{ display: 'none' }}>
                                <div class="div">
                                    <h3>{element.title}</h3>
                                    <h5>{element.description}</h5>
                                </div>
                                <button class="btn2" onClick={() => this.setDelete([element.id])}>Mark
                                    bug as fixed
                                </button>
                            </div>
                        </div>
                    )
                    : <p>You dont have valid prescriptions yet!</p>
            }
        </div>)
    }
}

export default ShowBugs;
