import React, { useState } from 'react'
import { ContinuousQrScanner } from 'react-webcam-qr-scanner.ts'

function ConnectToMachine() {
  const [qrCode, setQrCode] = useState('')

  function QrDetected(QrCode) {
    let machineIP;
    let message = '';
    try {
      machineIP = atob(QrCode);
    } catch (error) {
      message = "Invalid QR code"
    }


    let headers = new Headers();
    headers.set('Content-Type', 'application/json');
    if (QrCode !== '') {
      fetch(machineIP + "/configuration/status", {
        headers: headers
      })
        .then(response => response.json())
        .then(data => {

          if (data === true) {
            sessionStorage.setItem('machineIP', machineIP);
            window.location.href = ('/showPrescriptions');
          }
          else
            message = "Invalid QR code"
        })
        .catch(
          error => {
            message = "Invalid QR code"
          }
        );
        // message = "Invalid QR code"
    }
    return (message);
  }

  return (
    <div className='content'>
      <h1>
        Scan Machine QR Code: <code>{QrDetected(qrCode)}</code>
      </h1>
      <ContinuousQrScanner onQrCode={setQrCode} />
    </div>
  )
}

export default ConnectToMachine
